INSERT INTO `PREFIX_DB_aridius_review_news` (`review_id`, `parent_id`, `customer_id`, `news_id`, `author`, `text`, `status`, `date_added`, `date_modified`) VALUES ('9', '8', '0', '1', 'Алексей', 'Хороший вопрос. С опытом даже не задумываешься об этом как-то, просто чувствуешь. А вообще, главный критерий — пассивная шумоизоляция. Попробуйте заткнуть уши пальцами (мизинцами или указательными, если ушной канал пошире).', '1', '2022-05-01 12:20:08', '2022-05-01 12:20:16');
INSERT INTO `PREFIX_DB_aridius_review_news` (`review_id`, `parent_id`, `customer_id`, `news_id`, `author`, `text`, `status`, `date_added`, `date_modified`) VALUES ('10', '0', '0', '3', 'Маша', 'Как пользователь JBL Live 650BTNC могу сказать что со звуком порядок. Хотя чуть низы убрать надо в эквалайзере из фирменного приложения. Шумодавом топ. Так что эти верно тоже норм.', '1', '2022-05-01 12:21:35', '2022-05-01 12:22:06');
