INSERT INTO `PREFIX_DB_category_description` (`category_id`, `language_id`, `name`, `description`, `meta_title`, `meta_description`, `meta_keyword`, `meta_h1`) VALUES ('132', '1', 'Бытовая техника', '&lt;p&gt;К крупной бытовой технике относят крупные машины, используемые для повседневных хозяйственных задач, таких как приготовление пищи, стирка или сохранение продуктов питания, и которые, в отличие от сантехники, используют для своей работы электричество или топливо.&lt;br&gt;&lt;br&gt;Крупная бытовая техника отличается от мелкой (англ.)русск. размерами и весом. Крупные приборы могут иметь специальные электрические соединения, соединения с источниками газо- и водоснабжения, системами водоотведения и вентиляции, которые могут быть постоянно подключены к приборам. Это ограничивает их размещение в доме.&lt;br&gt;Современная газовая плита&lt;br&gt;&lt;br&gt;Многие крупные бытовые приборы изготовлены из листовой стали с эмалевым покрытием, которое в середине XX века было обычно белого цвета. Отсюда происхождение термина «белые товары» (англ. white goods),который получил распространение прежде всего в странах где говорят на британском английском, хотя определения термина «белые товары» могут различаться. В США термин «белые товары» также может относиться к белью. Поскольку крупные бытовые приборы в доме потребляют значительное количество энергии, страны и компании-производители стали большое внимание уделять повышению энергоэффективности. Повышение энергоэффективности может требовать изменений в конструкции приборов или улучшенных систем управления. &lt;br&gt;&lt;br&gt;&lt;/p&gt;', 'Техника для дома', 'Техника для дома', 'Техника для дома', 'Техника для дома');
