INSERT INTO `PREFIX_DB_product_description` (`product_id`, `language_id`, `name`, `description`, `tag`, `meta_title`, `meta_description`, `meta_keyword`, `meta_h1`) VALUES ('2268', '1', 'Apple Watch Series 6 GPS 40mm Blue1', '&lt;h2 style=&quot;text-align: center; &quot;&gt;Apple Watch Series 6&lt;/h2&gt; &lt;p&gt;Измеряйте уровень кислорода в крови. И следите за показателями физической активности на улучшенном, всегда включённом дисплее Retina.&lt;/p&gt; &lt;p&gt;&lt;img src=&quot;../image/catalog/description/ge-catalog-FotoOpis-Apple-Apple-Watch-Series-6-01.jpg&quot; alt=&quot;&quot; title=&quot;&quot;&gt;&lt;/p&gt; &lt;h2 style=&quot;text-align: center; &quot;&gt;Технологии из будущего&lt;/h2&gt; &lt;h2 style=&quot;text-align: center; &quot;&gt;Вам на пользу&lt;/h2&gt; &lt;p&gt;Измеряйте уровень кислорода в крови с помощью потрясающего нового датчика и специального приложения. Просматривайте показатели тренировок на улучшенном, всегда включённом дисплее Retina. Когда у вас Apple Watch Series 6, гораздо проще вести более здоровый образ жизни, быть активнее и оставаться на связи.&lt;/p&gt; &lt;p&gt;&lt;img src=&quot;../image/catalog/description/ge-catalog-FotoOpis-Apple-Apple-Watch-Series-6-02.jpg&quot; alt=&quot;&quot; title=&quot;&quot;&gt;&lt;/p&gt; &lt;h2 style=&quot;text-align: center; &quot;&gt;Уровень кислорода в крови&lt;/h2&gt; &lt;h3 style=&quot;text-align: center; &quot;&gt;Сияние инноваций&lt;/h3&gt; &lt;p&gt;Уровень кислорода в крови — это важный показатель вашего общего физического состояния. Он помогает оценить, насколько хорошо кровь насыщается кислородом и сколько кислорода поступает в организм. А измерить этот показатель теперь можно с помощью потрясающего нового датчика и специального приложения на Apple Watch Series 6. Они даже умеют делать это в фоновом режиме днём и ночью.&lt;/p&gt; &lt;p&gt;&lt;img src=&quot;../image/catalog/description/ge-catalog-FotoOpis-Apple-Apple-Watch-Series-6-03.jpg&quot; alt=&quot;&quot; title=&quot;&quot;&gt;&lt;/p&gt; &lt;h3 style=&quot;text-align: center; &quot;&gt;Новый датчик. Новые возможности.&lt;/h3&gt; &lt;p&gt;Новый датчик уровня кислорода в крови состоит из четырёх светодиодных кластеров и четырёх фотодиодов. Он встроен в заднюю панель часов, конструкция которой была полностью переос­мыс­лена. Вместе с приложением «Кислород в крови» он позволяет измерять уровень насыщения крови кислородом.&lt;/p&gt; &lt;p&gt;&lt;img src=&quot;../image/catalog/description/ge-catalog-FotoOpis-Apple-Apple-Watch-Series-6-05.jpg&quot; alt=&quot;&quot; title=&quot;&quot;&gt;&lt;/p&gt;', '', 'Смарт-часы Apple Watch Series 6 GPS 40mm Blue Aluminum Case with Deep Navy Sport Band (MG143)', 'Смарт-часы Apple Watch Series 6 GPS 40mm Blue Aluminum Case with Deep Navy Sport Band (MG143)', 'Смарт-часы Apple Watch Series 6 GPS 40mm Blue Aluminum Case with Deep Navy Sport Band (MG143)', 'Смарт-часы Apple Watch Series 6 GPS 40mm Blue Aluminum Case with Deep Navy Sport Band (MG143)');
